import React from "react";
import { useHistory } from "react-router-dom";

export const DeckSnapshot = ({
  deckId,
  name,
  description,
  amount,
  deleteHandler,
}) => {
  const history = useHistory();

  return (
    <div className="card">
      <div className="item">
        <div className="row">
          <div className="item">
            <h3>{name}</h3>
            <hr></hr>
          </div>
          <div className="circle">
            <h6>{amount} cards</h6>
          </div>
        </div>
        <p>{description}</p>
      </div>
      <div className="footer">
        <button
          className="btn btn-secondary ml-2 btn-sm"
          onClick={() => history.push(`/decks/${deckId}`)}
        >
          View
        </button>
        <button
          className="btn btn-primary ml-2 btn-sm"
          onClick={() => history.push(`/decks/${deckId}/study`)}
        >
          Study
        </button>
        <button className="btn btn-danger ml-2 btn-sm" onClick={() => deleteHandler(deckId)}>
          ðŸ—‘
        </button>
      </div>
    </div>
  );
};

export default DeckSnapshot;
