import React from "react";
import { Link, useRouteMatch } from "react-router-dom";

export const DeckButtons = ({ deck }) => (
    <article>
        <div className="mt-2">
            <Link className="btn btn-secondary btn-sm" to={`/decks/:${decks.id}`}>View</Link>
            <Link className="btn btn-primary btn-sm ml-2">Study</Link>
        </div>
    </article>
);

export default DeckButtons;
